(2, 1, 2, 'Scrum Theory', 'Teori Scrum', '2019-02-04 18:50:36', '2019-02-04 18:55:23'),
(3, 1, 2, 'Scrum Team', 'Tim Scrum', '2019-02-04 18:52:09', '2019-02-04 18:55:45'),
(4, 1, 2, 'Scrum Events', 'Upacara Scrum', '2019-02-04 18:52:09', '2019-02-04 18:56:09'),
(5, 1, 2, 'Scrum Artifacts', 'Artifak Scrum', '2019-02-04 18:52:09', '2019-02-04 18:56:16'),
(6, 1, 2, 'Other Scrum materials', 'Materi Lainnya', '2019-02-04 18:52:09', '2019-02-04 18:58:03'),
(7, 1, 2, 'Helpfull Links', 'Link Terkait', '2019-02-04 18:52:09', '2019-02-04 18:58:26');
